import React, { useEffect, useState } from 'react';
import './assets/styles/styles.css'; // Mengimpor file CSS
import { fetchTrips } from './api/api'; // Mengimpor fungsi untuk mengambil data
import MapComponent from './components/MapComponent'; // Mengimpor komponen peta
import FilterComponent from './components/FilterComponent'; // Mengimpor komponen filter
import ChartComponent from './components/ChartComponent'; // Mengimpor komponen grafik

const App = () => {
  const [trips, setTrips] = useState([]); // State untuk menyimpan data perjalanan
  const [filteredData, setFilteredData] = useState([]); // State untuk data yang difilter
  const [loading, setLoading] = useState(true); // State untuk menandakan loading
  const [error, setError] = useState(null); // State untuk menyimpan pesan kesalahan

  useEffect(() => {
    const loadTrips = async () => {
      try {
        setLoading(true); // Mengatur loading menjadi true
        const data = await fetchTrips({}); // Mengambil data perjalanan
        setTrips(data); // Menyimpan data perjalanan ke state
        setFilteredData(data); // Menyimpan data yang difilter ke state
      } catch (err) {
        setError('Failed to fetch trips'); // Menyimpan pesan kesalahan
        console.error('Error fetching trips:', err); // Menampilkan kesalahan di console
      } finally {
        setLoading(false); // Mengatur loading menjadi false
      }
    };
    loadTrips(); // Memanggil fungsi untuk mengambil data
  }, []);

  const handleFilter = async (filters) => {
    try {
      setLoading(true); // Mengatur loading menjadi true
      const data = await fetchTrips(filters); // Mengambil data berdasarkan filter
      setFilteredData(data); // Menyimpan data yang difilter ke state
    } catch (err) {
      setError('Failed to fetch filtered trips'); // Menyimpan pesan kesalahan
      console.error('Error fetching filtered trips:', err); // Menampilkan kesalahan di console
    } finally {
      setLoading(false); // Mengatur loading menjadi false
    }
  };

  if (loading) {
    return (
      <div className="loading">
        <h2>Loading...</h2> {/* Menampilkan pesan loading */}
      </div>
    );
  }

  if (error) {
    return (
      <div className="error-message">
        <h2>{error}</h2> {/* Menampilkan pesan kesalahan */}
      </div>
    );
  }

  return (
    <div className="container">
      <h1>
        <i className="fa fa-taxi" aria-hidden="true"></i> Dashboard Analitik Perjalanan Taksi
      </h1>
      <FilterComponent onFilter={handleFilter} /> {/* Komponen filter */}
      <MapComponent trips={filteredData} /> {/* Komponen peta */}
      <ChartComponent key={JSON.stringify(filteredData)} data={filteredData} /> {/* Komponen grafik */}
    </div>
  );
};

export default App;
