import React, { useEffect, useState } from 'react';
import './assets/styles/styles.css'; // Mengimpor file CSS untuk gaya
import { fetchTrips } from './api/api'; // Mengimpor fungsi untuk mengambil data perjalanan
import MapComponent from './components/MapComponent'; // Mengimpor komponen peta
import FilterComponent from './components/FilterComponent'; // Mengimpor komponen filter
import ChartComponent from './components/ChartComponent'; // Mengimpor komponen grafik

const App = () => {
  const [trips, setTrips] = useState([]); // Menyimpan data perjalanan
  const [filteredData, setFilteredData] = useState([]); // Menyimpan data yang sudah difilter
  const [loading, setLoading] = useState(true); // Menyimpan status loading
  const [error, setError] = useState(null); // Menyimpan pesan kesalahan

  // Mengambil data perjalanan saat komponen dimuat
  useEffect(() => {
    const loadTrips = async () => {
      try {
        setLoading(true);
        const data = await fetchTrips({});
        setTrips(data); // Menyimpan data perjalanan
        setFilteredData(data); // Menyimpan data perjalanan ke filteredData
      } catch (err) {
        setError('Failed to fetch trips');
        console.error('Error fetching trips:', err);
      } finally {
        setLoading(false);
      }
    };
    loadTrips();
  }, []);

  // Menangani filter yang diterapkan oleh pengguna
  const handleFilter = async (filters) => {
    try {
      setLoading(true);
      const data = await fetchTrips(filters);
      setFilteredData(data); // Menyimpan data yang sudah difilter
    } catch (err) {
      setError('Failed to fetch filtered trips');
      console.error('Error fetching filtered trips:', err);
    } finally {
      setLoading(false);
    }
  };

  // Menampilkan loading spinner saat data sedang dimuat
  if (loading) {
    return (
      <div className="loading">
        <h2>Loading...</h2>
      </div>
    );
  }

  // Menampilkan pesan kesalahan jika terjadi kesalahan
  if (error) {
    return (
      <div className="error-message">
        <h2>{error}</h2>
      </div>
    );
  }

  // Menampilkan konten utama aplikasi
  return (
    <div className="container">
      <h1>
        <i className="fa fa-taxi" aria-hidden="true"></i> Dashboard Analitik Perjalanan Taksi
      </h1>
      <FilterComponent onFilter={handleFilter} />
      <MapComponent trips={filteredData} />
      <ChartComponent key={JSON.stringify(filteredData)} data={filteredData} />
    </div>
  );
};

export default App;
